import React from 'react';

import {
  View,
  Text,
  StyleSheet,
} from 'react-native';

import MultipleChoiceList from 'components/TriviaCard/MultipleChoiceList';

const styles = StyleSheet.create({
  // container: {
  //   flex: 1,
