import React from 'react';
import {
  Text,
  View,
  StyleSheet,
  Button,
} from 'react-native';

// shared components
import Heading from 'components/shared/Heading';

import TriviaCard from 'components/shared/TriviaCard';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'lightblue', // '#ecf0f1',
    padding: 8,
    paddingTop: 50,
  },
  text: {
    margin: 24,
    fontSize: 18,
    // fontWeight: 'bold',
    textAlign: 'center',
  },
});

function PromptScreen({ navigation, route }) {
  // console.log('route.params: ', route.params);
  const { cardId, data } = route.params;
  // console.log('data[cardId].question: ', data[cardId].question);

  // Navigation methods
  const showResultsScreen = () => navigation.push('Results', {
    // cardId: cardId + 1,
    data,
  });
  const showNextPromptScreen = () => navigation.push('Prompt', {
    cardId: cardId + 1,
    data,
  });
  const view = (
    <View style={styles.container}>
      {/* Trivia Card */}
      <Heading title={`Question Number:${' '}${ cardId }`} />
      <TriviaCard item={data[cardId]} />
      {/*
        <Text style={styles.text}>
          Question:
          {' '}
          { data[cardId].question }
        </Text>
      */}
      <Button
        title="Go to next Prompt"
        onPress={() => {
          if (cardId >= data.length - 1) {
            // if end of list, show results screen to user
            showResultsScreen();
          } else {
            // else, show user next question
            showNextPromptScreen();
          }
        }}
      />
      {/*
        <Button title="Go to WelcomeScreen" onPress={() => navigation.navigate('Welcome')} />
        <Button title="Go back" onPress={() => navigation.goBack()} />
      */}
    </View>
  );
  return view;
}

export default PromptScreen;
