/*
FILENAME:     WelcomeScreen.js
PURPOSE:      Welcome screen show to user on beginning game
AUTHOR:       Eric Phung
CREATED:      10/26/2020 02:29 PM
*/
import React, { useState, useEffect } from 'react';
import {
  Text,
  View,
  StyleSheet,
  Button,
} from 'react-native';

// import data
import data from 'data/trivias.json';
// console.log('trivias: ', trivias);

// import functions
import uuidv4 from 'functions/uuidv4';

// shared components
import Heading from 'components/shared/Heading';
import Instructions from 'components/shared/Instructions';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // justifyContent: 'center',
    alignItems: 'center',
    // backgroundColor: 'lightblue', // '#ecf0f1',
    padding: 8,
    paddingTop: 50,
  },
  text: {
    margin: 24,
    fontSize: 18,
    // fontWeight: 'bold',
    textAlign: 'center',
  },
});

function WelcomeScreen({ navigation }) {
  const [answers, setAnswers] = useState([]);
  const [questions, setQuestions] = useState([]);
  // component did mount
  useEffect(() => {
    // set 10 (or global limit of) questions per round
    const list = [];
    data.forEach((item, index) => {
      if (index >= global.MAX_QUESTIONS_PER_ROUND) return;
      item.id = index;
      // return item
      list.push(item);
    });

    setQuestions(list);

    setAnswers([]);
  }, []);
  const view = (
    <View style={styles.container}>
      <Heading title="Tandem Trivia Game" />
      <Instructions title="Ea adipisicing aute adipisicing mollit excepteur pariatur labore in dolore esse dolor qui laborum dolore labore exercitation laboris non ad sint ullamco nostrud fugiat consectetur culpa veniam fugiat aliqua sed elit velit ut proident ea." />
      <View>
        <Button
          title="Start New Game"
          onPress={() => {
            // // format external provided data
            // const uniqueData = (data.map((item, index) => {
            //   // item.id = index
            //   return item
            // }))

            // get new, empty player results
            const results = {
              id: uuidv4(),
              answers,
            };
            navigation.push('Prompt', {
              cardId: 0,
              data: questions,
              results,
            })}
          }
        />

        <Text style={styles.text}>
          Developed by Eric Phung
          {'\n'}
          App Version:
          {' '}
          { global.appVersion }
        </Text>
      </View>
    </View>
  );
  return view;
}

export default WelcomeScreen;
