import React from 'react';

import {
  View,
  Text,
} from 'react-native';

const TriviaCard = ({
  item,
}) => {
  const view = (
    <View
      style={{
        height: '75%',
        width: '100%',

        borderWidth: 1,
        borderColor: 'red',
        borderStyle: 'solid',
      }}
    >
      <View style={{ flex: 1, backgroundColor: 'powderblue' }} />
      {/*
        <View style={{ flex: 1, backgroundColor: 'skyblue' }} />
      */}
      <View style={{ flex: 1, backgroundColor: 'steelblue' }} />

    </View>
  );
  return view;
};

export default TriviaCard;
