/*
FILENAME:     TriviaCard.js
PURPOSE:      View that displays trivia question and answers
AUTHOR:       Eric Phung
CREATED:      10/26/2020 03:27 PM
*/
import React from 'react';

import {
  View,
  Text,
  StyleSheet,
} from 'react-native';

const styles = StyleSheet.create({
  // container: {
  //   flex: 1,
  //   // justifyContent: 'center',
  //   alignItems: 'center',
  //   backgroundColor: 'lightblue', // '#ecf0f1',
  //   padding: 8,
  //   paddingTop: 50,
  // },
  northPanel: { flex: 1, backgroundColor: 'powderblue' },
  southPanel: { flex: 1, backgroundColor: 'steelblue' },
  text: {
    margin: 24,
    fontSize: 18,
    // fontWeight: 'bold',
    textAlign: 'center',
  },
});

const AnswerOption = ({ title, index }) => {
  return (
    <Text style={styles.text}>
      { title }
    </Text>
  );
};

const TriviaCard = ({
  item,
}) => {
  const view = (
    <View
      style={{
        height: '75%',
        width: '100%',

        borderWidth: 1,
        borderColor: 'red',
        borderStyle: 'solid',
      }}
    >
      <View style={styles.northPanel}>
        <Text style={styles.text}>
          { item.question }
        </Text>
      </View>
      {/*
        <View style={{ flex: 1, backgroundColor: 'skyblue' }} />
      */}
      <View style={styles.southPanel}>
        <AnswerOption title={item.incorrect[0]} />
      </View>

    </View>
  );
  return view;
};

export default TriviaCard;
