/*
FILENAME:     WelcomeScreen.js
PURPOSE:      Welcome screen show to user on beginning game
AUTHOR:       Eric Phung
CREATED:      10/26/2020 02:29 PM
*/
import React from 'react';
import {
  Text,
  View,
  StyleSheet,
  Button,
} from 'react-native';

// shared components
import Heading from 'components/shared/Heading';

// screen components
import Instructions from 'components/Welcome/Instructions';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    // justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: 'lightblue', // '#ecf0f1',
    padding: 8,
    paddingTop: 50,
  },
   text: {
    margin: 24,
    fontSize: 22,
    // fontWeight: 'bold',
    textAlign: 'center',
  },
});

function WelcomeScreen({ navigation }) {
  const view = (
    <View style={styles.container}>
      <Heading title="Tandem Trivia Game" />
      <Instructions />
      <View>
        <Button
          title="Go to Prompt"
          onPress={() => navigation.push('Prompt', {
            cardId: 0,
          })}
        />

        <Text style={styles.text}>Developed by Eric Phung</Text>
      </View>
    </View>
  );
  return view;
}

export default WelcomeScreen;
